import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-iqamah',
  templateUrl: './iqamah.component.html',
  styleUrls: ['./iqamah.component.css']
})
export class IqamahComponent {

  duration = 15;

  periods: any = [
    {
      name: 'Subuh',
      checked: false,
      src: '../../assets/images/template_1.png',
      tillIqamah: 5,
      text: ''
    },
    {
      name: 'Syuruq',
      checked: false,
      src: '../../assets/images/template_1.png',
      tillIqamah: 5,
      text: ''
    },
    {
      name: 'Zohor',
      checked: false,
      src: '../../assets/images/template_1.png',
      tillIqamah: 5,
      text: ''
    },
    {
      name: 'Jumaat',
      checked: false,
      src: '../../assets/images/template_1.png',
      tillIqamah: 5,
      text: ''
    },
    {
      name: 'Asar',
      checked: false,
      src: '../../assets/images/template_1.png',
      tillIqamah: 5,
      text: ''
    },
  ];

  constructor(
    private route: Router,
  ){}

  backToMenu(){
    this.route.navigate(['setting']);
  }

}
