import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { PrayerTimeService } from 'src/shared/proxy/prayertime/prayertime.service';

@Component({
  selector: 'app-islamic-calendar',
  templateUrl: './islamic-calendar.component.html',
  styleUrls: ['./islamic-calendar.component.css']
})
export class IslamicCalendarComponent{

  fileToUpload: File | null = null;

  fileName: string = '';

  constructor(
    private route: Router,
    private prayertimeService: PrayerTimeService
  ){}

  handleFileInput(files: any) {
    this.fileToUpload = files.target.files.item(0);
    this.fileName = files.target.files.item(0).name;
  }

  backToMenu(){
    this.route.navigate(['menu']);
  }

  uploadfile(){
    const formData = new FormData();

    if(this.fileToUpload) formData.append("file", this.fileToUpload);
    formData.append("title", this.fileName);

    this.prayertimeService.create(formData).subscribe((data: any) => {
      this.fileToUpload = null;
      this.fileName = '';

    })

  }

}
