import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-alarm',
  templateUrl: './alarm.component.html',
  styleUrls: ['./alarm.component.css']
})
export class AlarmComponent {

  periods: any = [
    {
      name: '',
      file: '',
    }
  ];

  constructor(
    private route: Router,
  ){}

  backToMenu(){
    this.route.navigate(['menu']);
  }

}
