import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { MosqueProfileService } from 'src/shared/proxy/mosqueprofile/mosqueprofile.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {

  selectedLogoURL = '../../assets/media/bg1.jpg';
  selectedLogoName = 'bg1.png';

  mosqueprofile: any;

  constructor(
    private route: Router,
    private mosqueprofileService: MosqueProfileService,
    private medialibraryService: MediaLibraryService, 
  ){}

  logos =[
    {
      url: '../../assets/media/bg1.jpg',
      name: 'bg1.jpg',
    },
    {
      url: '../../assets/media/logo.png',
      name: 'logo.png',
    },
    {
      url: '../../assets/media/bg2.jpg',
      name: 'bg2.jpg',
    },
  ]

  ngOnInit(): void {
    this.mosqueprofile.get(1).subscribe((data: any) => {
      this.mosqueprofile = data;
    });
  }

  selectedLogo(index: number){
    this.selectedLogoURL = this.logos[index].url;
    this.selectedLogoName = this.logos[index].name;
  }

  backToMenu(){
    this.route.navigate(['setting']);
  }

}
