      file: 'bg1',
      tillIqamah: 5,
    },
    {
      name: 'Jumaat',
      src: '../../assets/media/bg1.jpg',
      file: 'bg1',
      tillIqamah: 5,
    },
    {
      name: 'Asar',
      src: '../../assets/media/bg1.jpg',
      file: 'bg1',
      tillIqamah: 5,
      text: ''
    },
  ];

  tillIqamah: any = [
    {
      value: 5,
