    },
    {
      value: 19,
      text: '19 minit',
    },
    {
      value: 20,
      text: '20 minit',
    },
  ];

  available_images = [
    {
      src:'../../assets/media/bg1.jpg',
      file: 'bg1'
    },
    {
      src:'../../assets/media/bg2.jpg',
      file: 'bg2'
    },
    {
      src:'../../assets/media/bg1.jpg',
      file: 'bg1'
    }
  ];

  constructor(
    private route: Router,
  ){}

  backToMenu(){
    this.route.navigate(['setting']);
  }

  selectedImage(index: number, src: string, file: string){
    this.periods[index].src = src;
    this.periods[index].file = file;
  }

}
