import { Component, ElementRef, OnInit, ViewEncapsulation } from '@angular/core';
import { appFunc } from '../_models/appFunc';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
  encapsulation: ViewEncapsulation.None, //Used to overwrite parent component css
})
export class DashboardComponent implements OnInit {

  template = 1;
  bgImg = '';
  counter = 0;
  img_duration = 15000;

  isFullScreen = false;

  img_interval: any;

  //Call API in this component
  images = [
    {
      val: 'bg1',
      order: 0,
    },
    {
      val: 'bg2',
      order: 2,
    },
    {
      val: 'bg3',
      order: 1,
    },
  ];

  periods = [
    {
      period: 'Imsak',
      time: '05:52',
      order: 0,
    },
    {
      period: 'Subuh',
      time: '07:07',
      order: 1,
    },
    {
      period: 'Syuruk',
      time: '12:20',
      order: 2,
    },
    {
      period: 'Zohor',
      time: '16:46',
      order: 3,
    },
    {
      period: 'Asar',
      time: '19:31',
      order: 4,
    },
    {
      period: 'Maghrib',
      time: '20:47',
      order: 5,
    },
    {
      period: 'Isyak',
      time: '02:25',
      order: 6,
    },
  ]


  constructor(
    private elementRef: ElementRef,
    private backgroundimageService: BackgroundImageService,
  ){}

  ngOnDestroy(): void{
    clearInterval(this.img_interval);
  }

  ngOnInit(): void {

    this.images = appFunc.sortArr(this.images);

    this.imgCycle();
    this.img_interval = setInterval(() => {
      this.imgCycle()
    }, this.img_duration);
  }

  imgCycle(){
    this.bgImg = this.images[this.counter].val;
    this.elementRef.nativeElement.ownerDocument.body.style.setProperty("background-image", `url(../../assets/images/${this.bgImg}.jpg)`);
    if (this.counter == this.images.length - 1){
      this.counter = -1;
    }
    this.counter++;
  }



}
