import { Component, OnInit } from '@angular/core';
import { interval } from 'rxjs';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {

  weekday_bm = [
    "Ahad",
    "Isnin",
    "Selasa",
    "Rabu",
    "Khamis",
    "Jumaat",
    "Sabtu"
  ];
  month_bm = [
    "Januari", 
    "Februari", 
    "Mac", 
    "April", 
    "Mei", 
    "Jun", 
    "Julai", 
    "Ogos", 
    "September", 
    "Oktober", 
    "November", 
    "Disember"
  ]

  date: any;
  today_day = "";
  today_date = "";
  today_month = "";
  today_year = "";
  time_now: number | undefined;

  mosque_name = "";
  mosque_info = "";

  time_interval: any;

  ngOnInit(): void {

    this.mosque_name = "MASJID ARRAHMAH";
    this.mosque_info = "KARIAH SG BELIDA GUAR CHEMPEDAK KEDAH" + " | " + "03-65472344";

    this.time_interval = setInterval(() => {
      this.date = new Date();
      this.time_now = Date.now();
      this.today_day = this.weekday_bm[this.date.getDay()];

      this.today_date = this.date.getDate();
      this.today_year = this.date.getFullYear();
      this.today_month = this.month_bm[this.date.getMonth()];

    }, 1000);
  }
}
<header>
    <div class="container-fluid">
        <div class="row">
            <div class="col-md-3 banner_container left_banner">
                <div class="row">
                    {{today_day}}
                </div>
                <div class="row">
                    {{today_date}} {{today_month}} {{today_year}}
                </div>
                <div class="row">
                    09 Rabiulawal 1444
                </div>
            </div>
            <div class="col-md-6 banner_container">
                <div class="row middle_banner">
                    <div class="col-md-2">
                        <img src="../../assets/images/logo.png" alt="logo" width="120" height="120">
                    </div>
                    <div class="col-md-10">
                        <div class="row mosque_name">
                            {{mosque_name}}
                        </div>
                        <div class="row mosque_info">
                            {{mosque_info}}
                        </div>
                    </div>
                </div>
            </div>
            <div class="col-md-3 banner_container right_banner">
                <div class="row">
                    <div class="col-md-12 currtime">
                        {{ time_now | date:'mediumTime'}}
                    </div>
                </div>
            </div>
        </div>
    </div>
</header>
