import { KeyValue } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AppSettingService } from 'src/shared/proxy/appsetting/appsetting.service';

@Component({
  selector: 'app-text',
  templateUrl: './text.component.html',
  styleUrls: ['./text.component.css']
})
export class TextComponent implements OnInit {

  duration = 15;

  text = '';

  appsetting: any;

  constructor(
    private route: Router,
    private scrollingtextService: ScrollingTextService,
    private appsettingService: AppSettingService,
  ){}

  valueAscOrder = (a: KeyValue<number,string>, b: KeyValue<number,string>): number => {
    return a.value.localeCompare(b.value);
  }

  texts = [
    {
      text: 'a',
      order: 0,
      edit: false,
    },
    {
      text: 'b',
      order: 1,
      edit: false,
    },
    {
      text: 'c',
      order: 2,
      edit: false,
    },
    {
      text: 'd',
      order: 3,
      edit: false,
    },
    {
      text: 'e',
      order: 4,
      edit: false,
    },
    {
      text: 'f',
      order: 5,
      edit: false,
    },
    {
      text: 'g',
      order: 6,
      edit: false,
    },
    {
      text: 'h',
      order: 7,
      edit: false,
    },
    {
      text: 'i',
      order: 8,
      edit: false,
    },
    {
      text: 'j',
      order: 9,
      edit: false,
    },
  ]

  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }

  editText(index: number){
    this.texts[index].edit = true;
  }

  cancelEdit(index: number){
    this.texts[index].edit = false;
  }

  upSequence(index: number){
    const tempVal = this.texts[index];
    const i = this.texts.indexOf(tempVal);
    if (i > -1) { 
      this.texts.splice(index, 1);
    }
    const newIndex = index - 1;
    this.texts.splice(newIndex, 0, tempVal);
  }

  downSequence(index: number){
    const tempVal = this.texts[index];
    const i = this.texts.indexOf(tempVal);
    if (i > -1) { 
      this.texts.splice(index, 1);
    }
    const newIndex = index + 1;
    this.texts.splice(newIndex, 0, tempVal);
  }

  add_text(){
    this.texts.push({
      text: this.text,
      order: this.texts.length,
      edit: false,
    });

    this.text = '';
  }

  inputting(event: any){
    this.text = event.target.value;
  }

  backToMenu(){
    this.route.navigate(['menu']);
  }

}
