import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-iqamah',
  templateUrl: './iqamah.component.html',
  styleUrls: ['./iqamah.component.css']
})
export class IqamahComponent {

  duration = 15;

  periods: any = [
    {
      name: 'Subuh',
      checked: false,
      src: '../../assets/media/bg1.png',
      file: 'bg1',
      tillIqamah: 5,
      text: ''
    },
    {
      name: 'Syuruq',
      checked: false,
      src: '../../assets/media/bg1.png',
      file: 'bg1',
      tillIqamah: 5,
      text: ''
    },
    {
      name: 'Zohor',
      checked: false,
      src: '../../assets/media/bg1.png',
      file: 'bg1',
      tillIqamah: 5,
      text: ''
    },
    {
      name: 'Jumaat',
      checked: true,
      src: '../../assets/media/bg1.png',
      file: 'bg1',
      tillIqamah: 5,
      text: ''
    },
    {
      name: 'Asar',
      checked: false,
      src: '../../assets/media/bg1.png',
      file: 'bg1',
      tillIqamah: 5,
      text: ''
    },
  ];

  tillIqamah: any = [
    {
      value: 5,
      text: '5 minit',
    },
    {
      value: 6,
      text: '6 minit',
    },
    {
      value: 7,
      text: '7 minit',
    },
    {
      value: 8,
      text: '8 minit',
    },
    {
      value: 9,
      text: '9 minit',
    },
    {
      value: 10,
      text: '10 minit',
    },
    {
      value: 11,
