    },
    {
      value: 12,
      text: '12 minit',
    },
    {
      value: 13,
      text: '13 minit',
    },
    {
      value: 14,
      text: '14 minit',
    },
    {
      value: 15,
      text: '15 minit',
    },
    {
      value: 16,
      text: '16 minit',
    },
    {
      value: 17,
      text: '17 minit',
    },
    {
      value: 18,
      text: '18 minit',
    },
    {
      value: 19,
      text: '19 minit',
    },
    {
      value: 20,
      text: '20 minit',
    },
  ];

  available_images = [
    {
      src:'../../assets/media/bg1.jpg',
      file: 'bg1'
    },
    {
      src:'../../assets/media/bg2.jpg',
      file: 'bg2'
    },
    {
      src:'../../assets/media/bg1.jpg',
      file: 'bg1'
    }
  ]

  constructor(
    private route: Router,
  ){}

  backToMenu(){
    this.route.navigate(['setting']);
  }

  selectedImage(index: number, src: string, file: string){
    this.images[index].src = src;
    this.images[index].file = file;
  }

}
