import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-content',
  templateUrl: './content.component.html',
  styleUrls: ['./content.component.css']
})
export class ContentComponent implements OnInit {

  @Input() contents: any[] | undefined;
  current_text = '';
  counter = 1;

  ngOnInit(): void {
    this.content_text = '"Seseorang muslim itu adalah mereka yang membuatkan muslim yang lain selamat dari kejahatan lidahnya dan juga tangannya.."';
  }

  textCycle() {
    this.current_text = this.scrolling_texts[this.counter].text;
    if (this.counter == this.scrolling_texts.length - 1) {
      this.counter = -1;
    }
    this.counter++;
  }



}
<div class="container-fluid">
    <div class="row content_body">
        <h1>{{content_text}}</h1>
    </div>
</div>
