import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-full-screen',
  templateUrl: './full-screen.component.html',
  styleUrls: ['./full-screen.component.css']
})
export class FullScreenComponent {
duration = 50;

  newImage: any =
  {
    file: '',
    src: '',
    order: 0,
    edit: false
  };

  available_images = [
    {
      src:'../../assets/media/bg1.jpg',
      file: 'bg1'
    },
    {
      src:'../../assets/media/bg2.jpg',
      file: 'bg2'
    },
    {
      src:'../../assets/media/bg1.jpg',
      file: 'bg1'
    }
  ]

  images = [
    {
      file:'bg1',
      src: '../../assets/media/bg1.jpg',
      order: '',
      edit:false
    },
    {
      file:'bg1',
      src: '../../assets/media/bg1.jpg',
      order: '',
      edit:false
    },
    {
      file:'bg1',
      src: '../../assets/media/bg1.jpg',
      order: '',
      edit:false
    },
    {
      file:'bg1',
      src: '../../assets/media/bg1.jpg',
      order: '',
      edit:false
    },
    {
      file:'bg1',
      src: '../../assets/media/bg1.jpg',
      order: '',
      edit:false
    },
    {
      file:'bg1',
      src: '../../assets/media/bg1.jpg',
      order: '',
      edit:false
    },
    {
      file:'bg1',
      src: '../../assets/media/bg1.jpg',
      order: '',
      edit:false
    }
  ];

  constructor(
    private route: Router,
  ){}

  backToMenu(){
    this.route.navigate(['menu']);
  }

  editItem(index: number){
    this.images[index].edit = !this.images[index].edit;
  }

  deleteItem(index: number){
    this.images.splice(index, 1);
  }

  selectedImage(index: number, src: string, file: string){
    this.images[index].src = src;
    this.images[index].file = file;
  }

  selectedNewImage(src: string, file: string){
    let lastIndex = this.images.length + 1;
    this.newImage = 
    {
      file: file,
      src: src,
      order: lastIndex,
      edit: false
    }
  }

  addNewImage(){
    this.images.push(this.newImage);
    this.newImage = 
    {
      file: '',
      src: '',
      order: 0,
      edit: false
    }
  }

  upSequence(index: number){
    const tempVal = this.images[index];
    const i = this.images.indexOf(tempVal);
    if (i > -1) { 
      this.images.splice(index, 1);
    }
    const newIndex = index - 1;
    this.images.splice(newIndex, 0, tempVal);
  }

  downSequence(index: number){
    const tempVal = this.images[index];
    const i = this.images.indexOf(tempVal);
    if (i > -1) { 
      this.images.splice(index, 1);
    }
    const newIndex = index + 1;
    this.images.splice(newIndex, 0, tempVal);
  }
}
