import { Component, ElementRef, OnInit, ViewEncapsulation } from '@angular/core';
import { appFunc } from '../_models/appFunc';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
  encapsulation: ViewEncapsulation.None, //Used to overwrite parent component css
})
export class DashboardComponent implements OnInit {

  template = 1;
  bgImg = '';
  counter = 0;
  img_duration = 15000;

  img_interval: any;

  images = [
    {
      val: 'bg1',
      order: 0,
    },
    {
      val: 'bg2',
      order: 2,
    },
    {
      val: 'bg3',
      order: 1,
    },
  ];

  periods = {
    imsak: '05:52',
    subuh: '07:07',
    syuruk: '12:20',
    zohor: '16:46',
    asar: '19:31',
    maghrib: '20:47',
    isyak: '02:25'
  }


  constructor(private elementRef: ElementRef) { }


  ngOnInit(): void {

    this.images = appFunc.sortArr(this.images);

    this.imgCycle();
    this.img_interval = setInterval(() => {
      this.imgCycle()
    }, this.img_duration);
  }

  imgCycle(){
    this.bgImg = this.images[this.counter].val;
    this.elementRef.nativeElement.ownerDocument.body.style.setProperty("background-image", `url(../../assets/images/${this.bgImg}.jpg)`);
    if (this.counter == this.images.length - 1){
      this.counter = -1;
    }
    this.counter++;
  }



}
