import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-content',
  templateUrl: './content.component.html',
  styleUrls: ['./content.component.css']
})
export class ContentComponent implements OnInit {

  @Input() contents = [
    {
      val: '',
      order: 0,
    },
  ];
  current_text = '';
  counter = 1;
  text_interval: any;
  trans = true;

  ngOnInit(): void {
    this.textCycle();
    this.text_interval = setInterval(() => {
      this.textCycle();
    }, 2500);
  }

  textCycle() {
    this.trans = false
    setTimeout(() => {
      this.current_text = this.contents[this.counter].val;
      if (this.counter == this.contents.length - 1) {
        this.counter = -1;
      }
      this.counter++;
      this.trans = true;
    }, 500);

  }



}
