  selector: 'app-alarm',
  templateUrl: './alarm.component.html',
  styleUrls: ['./alarm.component.css']
})
export class AlarmComponent {

  constructor(
    private route: Router,
  ){}

  backToMenu(){
    this.route.navigate(['menu']);
  }

}
