import { Component, OnInit } from '@angular/core';
import { SolatService } from '../solat.service';
import { appFunc } from '../_models/appFunc';
import { ScrollingTextService } from 'src/shared/proxy/scrollingtext/scrollingtext.service';

@Component({
  selector: 'app-content',
  templateUrl: './content.component.html',
  styleUrls: ['./content.component.css']
})
export class ContentComponent implements OnInit {

  //Call API in this component
  contents = [
    {
      val: '"Seseorang muslim itu adalah mereka yang membuatkan muslim yang lain selamat dari kejahatan lidahnya dan juga tangannya.."',
      order: 0,
    },
    {
      val: '"Apabila kita mendoakan orang lain, maka akan hadir bersama malaikat yang akan mendoakan yang sama untuk kita.."',
      order: 2,
    },
    {
      val: '"Barang siapa yang menghendaki pahala dunia, maka di sisi Allah pahala dunia dan akhirat.."',
      order: 1,
    }
  ]

  current_text = '';

  counter = 1;

  text_interval: any;

  trans = true;

  constructor(
    private scrollingtextService : ScrollingTextService,
  ){}

  ngOnDestroy(): void{
    clearInterval(this.text_interval);
  }

  ngOnInit(): void {

    this.scrollingtextService.getall_st(')'

    this.contents = appFunc.sortArr(this.contents);

    this.textCycle();
    this.text_interval = setInterval(() => {
      this.textCycle();
    }, 15000);
  }

  textCycle() {
    this.trans = false
    setTimeout(() => {
      this.current_text = this.contents[this.counter].val;
      if (this.counter == this.contents.length - 1) {
        this.counter = -1;
      }
      this.counter++;
      this.trans = true;
    }, 250);

  }



}
<div class="container-fluid">
    <div class="row content_body">
        <h1 [ngClass]="trans ? 'unhide' : 'hide'">{{current_text}}</h1>
    </div>
</div>
