import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-alarm',
  templateUrl: './alarm.component.html',
  styleUrls: ['./alarm.component.css']
})
export class AlarmComponent {

  periods: any = [
    {
      name: 'Subuh',
      file: 'videoplayback',
    },
    {
      name: 'Syuruq',
      file: 'videoplayback',
    },
    {
      name: 'Zohor',
      file: 'videoplayback',
    },
    {
      name: 'Jumaat',
      file: 'videoplayback',
    },
    {
      name: 'Asar',
      file: 'videoplayback',
    },
  ];

  alarms: any = [
    {
      name: 'videoplayback',
      path: '../../assets/media/videoplayback.mp4',
    }
  ]

  constructor(
    private route: Router,
  ){}

  backToMenu(){
    this.route.navigate(['menu']);
  }

}
