import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {

  selectedLogoURL = '';
  selectedLogoName = '';

  logos =[
    {
      url: '../../assets/media/bg1.jpg',
      name: 'bg1.jpg',
    },
    {
      url: '../../assets/media/logo.png',
      name: 'logo.png',
    },
    {
      url: '../../assets/media/bg2.jpg',
      name: 'bg2.jpg',
    },
  ]

  ngOnInit(): void {

  }

  selectedLogo(index: number){
    this.selectedLogoURL = this.logos[index].url;
    this.selectedLogoName = this.logos[index].name;
  }

}
