import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-period',
  templateUrl: './period.component.html',
  styleUrls: ['./period.component.css']
})
export class PeriodComponent implements OnInit {

  periods = {
    imsak: '05:52',
    subuh: '07:07',
    syuruk: '01:20',
    zohor: '04:46',
    asar: '07:31',
    maghrib: '08:47',
    isyak: '02:25'
  }

  ngOnInit(): void {
  }

}
