import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent implements OnInit {



  scrolling_texts = [
    {
      text: "Sebaik-baik manusia adalah orang yang belajar Al-Qur'an & mengajarkannya1.",
      order: 0
    },
    {
      text: "Sebaik-baik manusia adalah orang yang belajar Al-Qur'an & mengajarkannya2.",
      order: 2
    },
    {
      text: "Sebaik-baik manusia adalah orang yang belajar Al-Qur'an & mengajarkannya3.",
      order: 1
    }
  ]
  current_text = "";

  text_interval: any;

  counter = 0;

  ngOnInit(): void {

    //Sorting based on order
    this.scrolling_texts.sort((a, b) => a.order - b.order);

    this.textCycle();
    this.text_interval = setInterval(() => {
      this.textCycle();
    }, 15000);
  }

  textCycle() {
    this.current_text = this.scrolling_texts[this.counter].text;
    if (this.counter == this.scrolling_texts.length - 1) {
      this.counter = -1;
    }
    this.counter++;
  }

}
<footer>
    <div class="container-fluid">
        <div class="row">
            <div class="col-md-4 info_left">
                Maghrib -01:05:44
            </div>
            <div class="col-md-4"></div>
            <div class="col-md-4 info_right">
                Maulidur rasul 3 hari lagi
            </div>
        </div>
        <div class="row">
            <div class="col-md-12">
                <table>
                    <tr>
                        <th class="imsak_h">Imsak</th>
                        <th class="subuh_h">Subuh</th>
                        <th class="syuruk_h">Syuruk</th>
                        <th class="zohor_h">Zohor</th>
                        <th class="asar_h">Asar</th>
                        <th class="maghrib_h">Maghrib</th>
                        <th class="isyak_h">Isyak</th>
                    </tr>
                    <tr>
                        <td class="imsak_t">
                            {{imsak_val}}
                        </td>
                        <td class="subuh_t">
                            {{subuh_val}}
                        </td>
                        <td class="syuruk_t">
                            {{syuruk_val}}
                        </td>
                        <td class="zohor_t">
                            {{zohor_val}}
                        </td>
                        <td class="asar_t">
                            {{asar_val}}
                        </td>
                        <td class="maghrib_t">
                            {{maghrib_val}}
                        </td>
                        <td class="isyak_t">
                            {{isyak_val}}
                        </td>
                    </tr>
                </table>
            </div>
        </div>
        <div class="row">
            <div class="col-md-12 scrolling_container">
                <div class="scrolling_text">
                    {{current_text}}
                </div>
            </div>
        </div>
    </div>
</footer>
